import React, { Component, useState } from 'react';
import { Row, Col, Image, Container, Card, Button} from "react-bootstrap";
import EncoreBlack from './images/balloons/EncoreBlack.jpg';
import axios from 'axios';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";

const Event = props =>(
  <Col className="d-flex align-items-stretch">
      <Card className = "cards" style={{ width: '18rem' }}>
          <Card.Img variant="top" src={EncoreBlack}/>
            <Card.Body>
              <Card.Title>props.event.eventname</Card.Title>
              <Card.Text>

              </Card.Text>
            </Card.Body>
      </Card>
  </Col>
)



export default class Events extends Component {

  constructor(props) {
    super(props);
    this.state = { events:[]};
  }

  componentDidMount(){
    axios.get('http://localhost:3000/Events/')
    .then(response => {
      this.setState({events: response.data })
    })
    .catch((error) =>{
      console.log(error);
    })
  }

  eventList(){
    return this.stats.evens.map(currentevent =>{
      return <Events event = {currentevent} />;
    })
  }

  render() {
    return (
      <div>
        <h1>
        Events
        </h1>

      {/*cards for display*/}




      </div>
    )
  }
}
